mapper.select:
select distinct t3.sysuser_id as userId from account t inner join accountprocess t1 on t.id = t1.account_id inner join act_ru_task t2 on t1.procinsetid = t2.proc_inst_id_ inner join sysuser_role t3 on t2.assignee_ = t3.roles_id where 1=1
and t.freq = #{freq}
and t.term = #{term}

select distinct a.* from (select CAST(t8.id_ AS char) as id,t10.institutionId,t10.institutionName,t10.templateCode as templateId,t10.templateName,DATE_FORMAT(STR_TO_DATE(t10.term, '%Y-%m-%d'), '%Y-%m-%d') as term,CAST(t8.name_ AS char) as processName,t10.validateStatus,CAST(t8.id_ AS char) as taskId,t9.ACCOUNT_ID, CAST(t8.description_ AS char) as description, t10.freq,t9.procInsetId,CAST(t8.category_ AS char) as category,t10.accountState,t9.id pid,t10.id lid, t9.refuseCause from (select t7.proc_inst_id_, t7.assignee_, t7.id_, t7.name_, t7.task_def_key_, t7.description_, t7.CATEGORY_, case when t7.category_ = 'bulu' then 1 when t7.category_ = 'shenhe' then 2 end as operationtype, IFNULL(t6.assignerroles_id,t7.assignee_) as roleId from act_ru_task t7 LEFT join RoleTask_role t6 on cast(t6.roletask_id as char) = cast(t7.assignee_ as char) where instr(t7.assignee_,'_') <= 0 ) t8 inner join accountprocess t9 on t8.proc_inst_id_ = t9.procinsetid inner join (SELECT LR.id, IT.ID AS INSTITUTIONID, IT.INSTITUTIONNAME, LRT.ID as reportTemplateId, LRT.TEMPLATENAME, LRT.TEMPLATECODE, date_format(str_to_date(LR.TERM, '%Y%m%d'), '%Y-%m-%d') AS term, RF.REPFREQNAME as freq, LR.ACCOUNTSTATE, LR.VALIDATESTATUS FROM ACCOUNT LR INNER JOIN INSTITUTION IT ON IT.ID = LR.INSTITUTION_ID INNER JOIN reporttemplate LRT ON LRT.ID = LR.ACCOUNTTEMPLATE_ID INNER JOIN BUSSYSTEM BS ON BS.ID = LRT.BUSSYSTEM_ID INNER JOIN S_REPFREQ RF ON LRT.REPFREQ_ID = RF.ID WHERE 1 = 1
and lrt.templateCode like concat(concat('%',#{templateCode,jdbcType=VARCHAR}),'%')
and lrt.templateName like concat(concat('%',#{templateName,jdbcType=VARCHAR}),'%')
and lr.freq= #{freq,jdbcType=VARCHAR}
and date_format(str_to_date(LR.TERM, '%Y%m%d'), '%Y-%m-%d')=#{term,jdbcType=VARCHAR}
and it.institutionName like concat(concat('%',#{institutionName,jdbcType=VARCHAR}),'%')
and lr.validatestatus = #{validatestatus}
and lr.accountState = #{accountState}
and bs.reportSubSystem_id = #{reportSubSystem_id,jdbcType=INTEGER}
and lr.institution_id in (#{orgIds})
and lr.institution_id = #{institution_id,jdbcType=INTEGER} ) t10 on t9.account_id = t10.id inner join (SELECT a.userId, a.roleId, A.reporttemplateid, a.operationtype, group_concat(A.orgs separator ',')as orgs FROM (select t1.id as userId, t3.id as roleId, inst.id as orgId, case when t5.institutelevel = 0 then inst.id when t5.institutelevel = 1 then (select group_concat(ss1.ID separator ',') from institution ss1 where ss1.parentinstitution_id = inst.id) when t5.institutelevel = 2 then queryChildrenInstitutionInfo(inst.id) end as orgs, t5.reporttemplateid, t5.operationtype from sysuser t1 inner join institution inst on t1.org_id = inst.id inner join sysuser_role t2 on t1.id = t2.sysuser_id inner join (select r.id from role r where r.subsystem_id = #{reportSubSystem_id,jdbcType=INTEGER}) t3 on t2.roles_id = t3.id inner join role_reportpermission t4 on t3.id = t4.role_id inner join reportpermission t5 on t4.reportpermission_id = t5.id where t1.id = #{sysuser_id,jdbcType=INTEGER}) A GROUP BY a.userId, a.roleId, A.reporttemplateid, A.operationtype) authority on find_in_set(t10.INSTITUTIONID,authority.orgs)>0 and authority.reporttemplateid = t10.reporttemplateId and cast(authority.roleId as char) = cast(t8.roleId as char) and authority.operationtype = t8.operationtype where 1=1
and t8.category_ = #{category_,jdbcType=VARCHAR}
and t8.proc_inst_id_ = #{proc_inst_id_,jdbcType=INTEGER}
and authority.userId = #{sysuser_id,jdbcType=INTEGER}
) a order by a.term desc,a.templateid,a.institutionid

select count(*) from ( select distinct t8.id_ from (select t7.proc_inst_id_, t7.assignee_, t7.id_, t7.name_, t7.task_def_key_, t7.description_, t7.CATEGORY_, case when t7.category_ = 'bulu' then 1 when t7.category_ = 'shenhe' then 2 end as operationtype, IFNULL(t6.assignerroles_id,t7.assignee_) as roleId from act_ru_task t7 LEFT join RoleTask_role t6 on cast(t6.roletask_id as char) = cast(t7.assignee_ as char) where instr(t7.assignee_,'_') <= 0 ) t8 inner join accountprocess t9 on t8.proc_inst_id_ = t9.procinsetid inner join (SELECT LR.id, IT.ID AS INSTITUTIONID, IT.INSTITUTIONNAME, LRT.ID as reportTemplateId, LRT.TEMPLATENAME, LRT.TEMPLATECODE, date_format(str_to_date(LR.TERM, '%Y%m%d'), '%Y-%m-%d') AS term, RF.REPFREQNAME as freq, LR.ACCOUNTSTATE, LR.VALIDATESTATUS FROM ACCOUNT LR INNER JOIN INSTITUTION IT ON IT.ID = LR.INSTITUTION_ID INNER JOIN reporttemplate LRT ON LRT.ID = LR.ACCOUNTTEMPLATE_ID INNER JOIN BUSSYSTEM BS ON BS.ID = LRT.BUSSYSTEM_ID INNER JOIN S_REPFREQ RF ON LRT.REPFREQ_ID = RF.ID WHERE 1 = 1
and lrt.templateCode like concat(concat('%',#{templateCode,jdbcType=VARCHAR}),'%')
and lrt.templateName like concat(concat('%',#{templateName,jdbcType=VARCHAR}),'%')
and lr.freq= #{freq,jdbcType=VARCHAR}
and date_format(str_to_date(LR.TERM, '%Y%m%d'), '%Y-%m-%d')=#{term,jdbcType=VARCHAR}
and it.institutionName like concat(concat('%',#{institutionName,jdbcType=VARCHAR}),'%')
and lr.validatestatus = #{validatestatus}
and lr.accountState = #{accountState}
and bs.reportSubSystem_id = #{reportSubSystem_id,jdbcType=INTEGER}
and lr.institution_id in (#{orgIds})
and lr.institution_id = #{institution_id,jdbcType=INTEGER} ) t10 on t9.account_id = t10.id inner join (SELECT a.userId, a.roleId, A.reporttemplateid, a.operationtype, group_concat(A.orgs separator ',')as orgs FROM (select t1.id as userId, t3.id as roleId, inst.id as orgId, case when t5.institutelevel = 0 then inst.id when t5.institutelevel = 1 then (select group_concat(ss1.ID separator ',') from institution ss1 where ss1.parentinstitution_id = inst.id) when t5.institutelevel = 2 then queryChildrenInstitutionInfo(inst.id) end as orgs, t5.reporttemplateid, t5.operationtype from sysuser t1 inner join institution inst on t1.org_id = inst.id inner join sysuser_role t2 on t1.id = t2.sysuser_id inner join (select r.id from role r where r.subsystem_id = #{reportSubSystem_id,jdbcType=INTEGER}) t3 on t2.roles_id = t3.id inner join role_reportpermission t4 on t3.id = t4.role_id inner join reportpermission t5 on t4.reportpermission_id = t5.id where t1.id = #{sysuser_id,jdbcType=INTEGER}) A GROUP BY a.userId, a.roleId, A.reporttemplateid, A.operationtype) authority on find_in_set(t10.INSTITUTIONID,authority.orgs)>0 and authority.reporttemplateid = t10.reporttemplateId and cast(authority.roleId as char) = cast(t8.roleId as char) and authority.operationtype = t8.operationtype where 1=1
and t8.category_ = #{category_,jdbcType=VARCHAR}
and t8.proc_inst_id_ = #{proc_inst_id_,jdbcType=INTEGER}
and authority.userId = #{sysuser_id,jdbcType=INTEGER}
) a

select o.id,it.institutionId,it.institutionName,lrt.templateCode,lrt.templateName, date_format(str_to_date(lr.term, '%Y%m%d'), '%Y-%m-%d') as term,CAST(task.name_ AS CHAR) AS NAME, lr.validateStatus,CAST(task.id_ AS CHAR ) as taskId,task.proc_inst_id_ as processId,task.end_time_,lr.freq ,task.userName as submitter from ( SELECT actRuTask.*, IF ( @procInstId = actRuTask.proc_inst_id_ ,@rank :=@rank + 1 ,@rank := 1 ) AS rn, @procInstId := actRuTask.proc_inst_id_ as procInstId FROM ( select t.id_ , t.name_ , t.end_time_, t.proc_inst_id_, s.userName from ACT_HI_TASKINST t left join sysuser s on t.assignee_=cast(s.id as char) where t.delete_reason_ = 'completed' order by t.proc_inst_id_,t.start_time_ desc ) actRuTask, ( SELECT @procInstId := NULL ,@rank := 0 ) a ) task inner join AccountProcess o on task.proc_inst_id_=o.procInsetId inner join Account lr on lr.id=o.ACCOUNT_ID inner join Institution it on it.id=lr.institution_id inner join reporttemplate lrt on lrt.id=lr.ACCOUNTTEMPLATE_ID inner join (SELECT a.userId, A.reporttemplateid, group_concat(A.orgs separator ',')as orgs FROM (select t1.id as userId, inst.id as orgId, case when t5.institutelevel = 0 then inst.id when t5.institutelevel = 1 then (select group_concat(ss1.ID separator ',') from institution ss1 where ss1.parentinstitution_id = inst.id) when t5.institutelevel = 2 then queryChildrenInstitutionInfo(inst.id) end as orgs, t5.reporttemplateid from sysuser t1 inner join institution inst on t1.org_id = inst.id inner join sysuser_role t2 on t1.id = t2.sysuser_id inner join (select r.id from role r where r.subsystem_id = #{reportSubSystem_id,jdbcType=INTEGER}) t3 on t2.roles_id = t3.id inner join role_reportpermission t4 on t3.id = t4.role_id inner join reportpermission t5 on t4.reportpermission_id = t5.id where t1.id = #{sysuser_id,jdbcType=INTEGER} and t5.operationtype = #{operationType} ) A GROUP BY a.userId,A.reporttemplateid) authority ON authority.reporttemplateid = lrt.id AND instr(authority.orgs, '['||it.id||']') > 0 inner join BusSystem bs on bs.id = lrt.busSystem_id and bs.reportsubsystem_id=#{reportSubSystem_id} where 1=1 and task.rn=1
and lrt.templateCode like concat(concat('%',#{templateCode}),'%')
and lrt.templateName like concat(concat('%',#{templateName}),'%')
and lr.freq= #{freq}
and date_format(str_to_date(lr.term, '%Y%m%d'), '%Y-%m-%d') = #{term,jdbcType=VARCHAR}
and it.institutionName like concat(concat('%',#{institutionName}),'%')
and task.proc_inst_id_ = #{processId}
and lr.institution_id = #{institution_id}
order by lrt.templateCode,lr.term

select count(1) from ( select task.proc_inst_id_ from ( select t.proc_inst_id_ from ACT_HI_TASKINST t where t.delete_reason_ = 'completed' ) task inner join AccountProcess o on task.proc_inst_id_=o.procInsetId inner join Account lr on lr.id=o.ACCOUNT_ID inner join Institution it on it.id=lr.institution_id inner join reporttemplate lrt on lrt.id=lr.ACCOUNTTEMPLATE_ID inner join (SELECT a.userId, A.reporttemplateid, group_concat(A.orgs separator ',')as orgs FROM (select t1.id as userId, inst.id as orgId, case when t5.institutelevel = 0 then inst.id when t5.institutelevel = 1 then (select group_concat(ss1.ID separator ',') from institution ss1 where ss1.parentinstitution_id = inst.id) when t5.institutelevel = 2 then queryChildrenInstitutionInfo(inst.id) end as orgs, t5.reporttemplateid from sysuser t1 inner join institution inst on t1.org_id = inst.id inner join sysuser_role t2 on t1.id = t2.sysuser_id inner join (select r.id from role r where r.subsystem_id = #{reportSubSystem_id,jdbcType=INTEGER}) t3 on t2.roles_id = t3.id inner join role_reportpermission t4 on t3.id = t4.role_id inner join reportpermission t5 on t4.reportpermission_id = t5.id where t1.id = #{sysuser_id,jdbcType=INTEGER} and t5.operationtype = #{operationType} ) A GROUP BY a.userId,A.reporttemplateid) authority ON authority.reporttemplateid = lrt.id AND instr(authority.orgs, '['||it.id||']') > 0 inner join BusSystem bs on bs.id = lrt.busSystem_id and bs.reportsubsystem_id=#{reportSubSystem_id} where 1=1
and lrt.templateCode like concat(concat('%',#{templateCode}),'%')
and lrt.templateName like concat(concat('%',#{templateName}),'%')
and lr.freq= #{freq}
and date_format(str_to_date(lr.term, '%Y%m%d'), '%Y-%m-%d') = #{term,jdbcType=VARCHAR}
and it.institutionName like concat(concat('%',#{institutionName}),'%')
and task.proc_inst_id_ = #{processId}
and lr.institution_id = #{institution_id}
group by task.proc_inst_id_) a
