mapper.select:
    	select i.institutionname,t.templatename,
    	date_format(str_to_date(a.term, '%Y%m%d'), '%Y-%m-%d') as term,
    	a.accountstate from ACCOUNT a left join INSTITUTION i on
		a.institution_id=i.id left join reporttemplate t on a.accounttemplate_id=t.id 
		where a.accountstate= #{accountState}  
		and date_format(str_to_date(a.term, '%Y%m%d'), '%Y-%m-%d') = #{term}
		and i.institutionname=#{institutionName}
