mapper.select:
select al.acccountId, al.accountName, al.institutionId, al.institutionName, date_format(str_to_date(al.term, '%Y%m%d'), '%Y-%m-%d') as term, al.editUser, al.editTime, al.editDate, ai.editBeforeValue, ai.editAfterValue, ai.fieldName, ai.fieldId from ACCOUNT_EDITlOG al, ACCOUNT_EDITlOGITEM ai where al.id = ai.accounteditlog_id
and al.accountName=#{accountName}
and al.institutionName like'%${institutionName}%'
and date_format(str_to_date(al.term, '%Y%m%d'), '%Y-%m-%d')=#{term}
and al.editUser like '%${editUser}%'
order by al.editTime DESC

select count(1) from ACCOUNT_EDITlOG al, ACCOUNT_EDITlOGITEM ai where al.id = ai.accounteditlog_id
and al.accountName=#{accountName}
and al.institutionName like'%${institutionName}%'
and date_format(str_to_date(al.term, '%Y%m%d'), '%Y-%m-%d')=#{term}
and al.editUser like '%${editUser}%'
order by al.editTime DESC

select IFNULL(s.username, t.editUser) as editUser, date_format(str_to_date(t.editDate, '%Y-%m-%d'), '%Y-%m-%d') as editDate, t.accountname, date_format(str_to_date(t.term, '%Y%m%d'), '%Y-%m-%d') as term, t.institutionname, t.editLineNum from (select t.editUser, date_format(str_to_date(t.editDate, '%Y-%m-%d'), '%Y-%m-%d') as editDate, t.accountName, date_format(str_to_date(t.term, '%Y%m%d'), '%Y-%m-%d') as term, t.institutionName, IFNULL(sum(t.editLineNum), 0) as editLineNum from ACCOUNT_EDITlOG t where 1 = 1
and t.accountName like'%${accountName}%'
and t.institutionName like'%${institutionName}%'
and date_format(str_to_date(t.term, '%Y%m%d'), '%Y-%m-%d')=#{term}
and t.editUser like '%${editUser}%'
and date_format(str_to_date(t.editDate, '%Y-%m-%d'), '%Y-%m-%d')=#{editDate}
group by t.editUser, t.editDate, t.accountName, t.term, t.institutionName
having sum(t.editLineNum) = ${editLineNum}
) t left join sysuser s on t.editUser = s.loginid order by t.editDate desc, t.term desc, t.editUser, t.accountName, t.institutionName

	select count(1) from (
	select 1 from ACCOUNT_EDITlOG t
	 where 1 = 1

		and t.accountName like'%${accountName}%'


		and t.institutionName like'%${institutionName}%'


		and date_format(str_to_date(t.term, '%Y%m%d'), '%Y-%m-%d')=#{term}


		and t.editUser like '%${editUser}%'


		and date_format(str_to_date(t.editDate, '%Y-%m-%d'), '%Y-%m-%d')=#{editDate}

 	group by t.editUser, t.editDate, t.accountName, t.term, t.institutionName

 		having sum(t.editLineNum) = ${editLineNum}

 ) a
