mapper.select:
select al.acccountId, al.accountName, al.institutionId, al.institutionName, to_char(to_date(al.term, 'yyyy-MM-dd'), 'yyyy-MM-dd') as term, al.editUser, al.editTime, al.editDate, ai.editBeforeValue, ai.editAfterValue, ai.fieldName, ai.fieldId from ACCOUNT_EDITlOG al, ACCOUNT_EDITlOGITEM ai where al.id = ai.accounteditlog_id
and al.accountName=#{accountName}
and al.institutionName like'%${institutionName}%'
and to_char(to_date(al.term,'yyyy-MM-dd'),'yyyy-MM-dd')=#{term}
and al.editUser like '%${editUser}%'
order by al.editTime DESC

select count(1) from ACCOUNT_EDITlOG al, ACCOUNT_EDITlOGITEM ai where al.id = ai.accounteditlog_id
and al.accountName=#{accountName}
and al.institutionName like'%${institutionName}%'
and to_char(to_date(al.term,'yyyy-MM-dd'),'yyyy-MM-dd')=#{term}
and al.editUser like '%${editUser}%'
order by al.editTime DESC

select t.editUser, to_char(to_date(t.editDate, 'yyyy-MM-dd'), 'yyyy-MM-dd') as editDate, t.accountName, to_char(to_date(t.term, 'yyyy-MM-dd'), 'yyyy-MM-dd') as term, t.institutionName, NVL(sum(t.editLineNum) ,0) as editLineNum from ACCOUNT_EDITlOG t where 1 = 1
and t.accountName like'%${accountName}%'
and t.institutionName like'%${institutionName}%'
and to_char(to_date(t.term,'yyyy-MM-dd'),'yyyy-MM-dd')=#{term}
and t.editUser like '%${editUser}%'
and to_char(to_date(t.editDate,'yyyy-MM-dd'),'yyyy-MM-dd')=#{editDate}
group by t.editUser, t.editDate, t.accountName, t.term, t.institutionName
having sum(t.editLineNum) = ${editLineNum}
order by t.editDate desc, t.term desc, t.editUser, t.accountName, t.institutionName

	select count(1) from (
	select 1 from ACCOUNT_EDITlOG t
	 where 1 = 1
	
		and t.accountName like'%${accountName}%'
	
	
		and t.institutionName like'%${institutionName}%'
	
	
		and to_char(to_date(t.term,'yyyy-MM-dd'),'yyyy-MM-dd')=#{term}
	
	
		and t.editUser like '%${editUser}%'
	
	
		and to_char(to_date(t.editDate,'yyyy-MM-dd'),'yyyy-MM-dd')=#{editDate}
	
 	group by t.editUser, t.editDate, t.accountName, t.term, t.institutionName
 	
 		having sum(t.editLineNum) = ${editLineNum}
 	
 )
